select a.machine_id, round(avg(b.timestamp - a.timestamp), 3) as processing_time from Activity a join Activity b on a.process_id = b.process_id where b.machine_id = a. machine_id and b.activity_type = 'end' and a.activity_type = 'start' group by a.machine_id; 